    public class LazyAssContext : DbContext
    {
        static LazyAssContext()
        {
            Database.SetInitializer(new DbInitializer());
        }

        public LazyAssContext()
            : base("LazyAssContext")
        {
        }

        public virtual DbSet<Episode> Episodes { get; set; }
        public virtual DbSet<Genre> Genres { get; set; }
        public virtual DbSet<GenresTvSeries> GenresTvSeries { get; set; }
        public virtual DbSet<Image> Images { get; set; }
        public virtual DbSet<LazyUser> LazyUser { get; set; }
        public virtual DbSet<LazyUserTvSeries> LazyUserTvSeries { get; set; }
        public virtual DbSet<Season> Seasons { get; set; }
        public virtual DbSet<TvSeries> TvSeries { get; set; }

    }

